import UIKit

class HistoryBuyPresenter: HistoryBuyPresenterProtocol {
   
    weak private var view: HistoryBuyViewProtocol?
    var interactor: HistoryBuyInteractorInputProtocol?
    private let router: HistoryBuyWireframeProtocol

    init(interface: HistoryBuyViewProtocol, interactor: HistoryBuyInteractorInputProtocol?, router: HistoryBuyWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }

    func getHistoryBuy(offset: Int) {
        interactor?.getHistoryBuy(offset: offset)
    }
}

extension HistoryBuyPresenter: HistoryBuyInteractorOutputProtocol {
    func getSucessHistoryBuy(history: [HistoryBuyEntity]) {
        view?.getSucessHistoryBuy(history: history)
    }
    
    func getErrorHistoryBuy(error: APIError?) {
        view?.getErrorHistoryBuy(error: error)
    }
}
