import UIKit

class HistoryCoinPresenter: HistoryCoinPresenterProtocol {
   
    weak private var view: HistoryCoinViewProtocol?
    var interactor: HistoryCoinInteractorInputProtocol?
    private let router: HistoryCoinWireframeProtocol

    init(interface: HistoryCoinViewProtocol, interactor: HistoryCoinInteractorInputProtocol?, router: HistoryCoinWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }

    func getHistoryCoin(offset: Int) {
        interactor?.getHistoryCoin(offset: offset)
    }
}

extension HistoryCoinPresenter: HistoryCoinInteractorOutputProtocol{
    func getSucessHistoryCoin(history: BaseHistoryCoinEntity?) {
        view?.getSucessHistoryCoin(history: history)
    }
    func getErrorHistoryCoin(error: APIError?) {
        view?.getErrorHistoryCoin(error: error)
    }
}
