import UIKit


class MySellExpiredDetailViewController: BaseViewController {

	var presenter: MySellExpiredDetailPresenterProtocol?
    
    @IBOutlet weak var bottomConstant: NSLayoutConstraint!
    @IBOutlet weak var heightConstant: NSLayoutConstraint!
    
    
    var record: RecordEntity? {
        didSet {
            tbDetail.reloadData()
        }
    }
    
    var recordId: String?
    
    var listHeader = ["Chi tiết", "Giới thiệu","Thông tin người bán", "Địa chỉ đăng  ", "Bình Luận"]
    
    var listComment = [CommentEntity]()
    
    @IBOutlet weak var tbDetail: UITableView!
    
    var tapGesture: UITapGestureRecognizer!

	override func viewDidLoad() {
        super.viewDidLoad()
        addKeyboardNotification()
        configureTable()
        
        presenter?.getDetail(id: recordId&)
    }
    
    override func viewWillDisappear(_ animated: Bool) {
        super.viewWillDisappear(animated)
        self.tabBarController?.tabBar.isHidden = false
    }
    
    override func setUpNavigation() {
        super.setUpNavigation()
        setTitleNavigation(title: "Chi tiết sản phẩm")
        addBackToNavigation()
        addButtonToNavigation(image: AppImage.imgShare, style: .right, action: #selector(btnShareTapped))
        self.tabBarController?.tabBar.isHidden = true
    }
    
    
    
    @objc func btnShareTapped() {
        ShareNativeHelper.shared.showShareLinkInstall(controller: self)
    }
    
}

extension MySellExpiredDetailViewController: MySellExpiredDetailViewProtocol {
    func didGetDetail(record: RecordEntity?) {
        self.record = record
    }
    
    func didDeleteRecord(data: BaseResponse?) {
        ProgressView.shared.hide()
        self.pop()
    }
}

extension MySellExpiredDetailViewController: MySellExpiredDetailImageCellDelegate {
    func btnDeleteTapped() {
        guard let recordID = record?.id else { return }
        PopUpHelper.shared.showYesNoQuestionHaveAds(question: "Bạn chắc chắn muốn xoá bài đăng này ?", completionYes: {
            ProgressView.shared.show()
            self.presenter?.deleteRecord(recordID: recordID)
        }) {
            
        }
    }
    
    func btnCopyTapped() {
        let vc = PostStepOneRouter.createModule(record: record)
        vc.isCopyUpdate = true
        self.navigationController?.push(controller: vc)
    }
}
