import UIKit

class NotificationPresenter: NotificationPresenterProtocol {

    weak private var view: NotificationViewProtocol?
    var interactor: NotificationInteractorInputProtocol?
    private let router: NotificationWireframeProtocol

    init(interface: NotificationViewProtocol, interactor: NotificationInteractorInputProtocol?, router: NotificationWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }
    
    func getNotification(offset: Int) {
        interactor?.getNotification(offset: offset)
    }
    
    func readNotification(id: String) {
        interactor?.readNotification(id: id)
    }

    func gotoLogin() {
        router.gotoLogin()
    }
}

extension NotificationPresenter: NotificationInteractorOutputProtocol {
   
    func didGetNotification(notifications: ParentNotificationEntity?) {
        view?.didGetNotification(notifications: notifications)
    }
    
    func didGetError(error: APIError?) {
        view?.didGetError(error: error)
    }
}
