import UIKit

class OrderBuyDetailRouter: OrderBuyDetailWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule(recordId: String?) -> OrderBuyDetailViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = OrderBuyDetailViewController(nibName: nil, bundle: nil)
        let interactor = OrderBuyDetailInteractor()
        let router = OrderBuyDetailRouter()
        let presenter = OrderBuyDetailPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        view.recordId = recordId
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
    
    func gotoLogin() {
        let vc = LoginRouter.createModule()
        let nc = UINavigationController(rootViewController: vc)
        viewController?.present(controller: nc, animated: true)
    }
}
