import UIKit

class SignUpPresenter: SignUpPresenterProtocol {
    func verifyPhone(verifyCode: String, phoneCode: String, phoneNum: String) {
        interactor?.verifyPhone(verifyCode: verifyCode, phoneCode: phoneCode, phoneNum: phoneNum)
    }
    

    weak private var view: SignUpViewProtocol?
    var interactor: SignUpInteractorInputProtocol?
    private let router: SignUpWireframeProtocol

    init(interface: SignUpViewProtocol, interactor: SignUpInteractorInputProtocol?, router: SignUpWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }
    
    func getCaptcha() {
        interactor?.getCaptcha()
    }
    
    func signUp(param: SignUpParam) {
        interactor?.signUp(param: param)
    }
    
    func gotoTermOfPolicy() {
        router.gotoTermOfPolicy()
    }
}

extension SignUpPresenter: SignUpInteractorOutputProtocol {
    func didVerifyPhone(response: UserEntity?) {
        view?.didVerifyPhone(response: response)
    }
    
    func didVerifyPhone(error: APIError?) {
        view?.didVerifyPhone(error: error)
    }
    
    func successCaptcha(image: UIImage) {
        view?.successCaptcha(image: image)
    }
    
    func signUpSuccess(user: UserEntity?) {
        view?.signUpSuccess(user: user)
    }
    
    func signUpError(error: APIError) {
        view?.signUpError(error: error)
    }
}
